mapper.select:
		SELECT n.*, m.name
           FROM (
                  SELECT b.*, ROW_NUMBER() OVER (ORDER BY bno) AS no
                 FROM (
                       SELECT * FROM namu_board 

                        ) b
                ) n
						LEFT JOIN namu_member m ON n.writer = m.id
						ORDER BY n.no DESC
						LIMIT #{beginList}, #{endList}

SELECT b.*, (SELECT name FROM member m WHERE m.id= b.writer) name
FROM board b
WHERE id=#{id}
